{"changed":true,"filter":false,"title":"application_helper.rb","tooltip":"/board/app/helpers/application_helper.rb","value":"module ApplicationHelper\n    \n    def bootstrap_class_for(flash_type) #bootstrap 3 문서 참조\n    case flash_type\n      when \"success\"\n        \"alert-success\"   # 초록색\n      when \"error\"\n        \"alert-danger\"    # 빨간색\n      when \"alert\"\n        \"alert-warning\"   # 노랑색\n      when \"notice\"\n        \"alert-info\"      # 파랑색\n      else\n        flash_type.to_s\n    end\n  end\n  \n  def icon(font)\n    \"<span class='glyphicon glyphicon-#{font}'></span>\".html_safe\n  end\n  \n  def tag_icons(tag_list)\n  tag_list.map do | tag |\n    \"<a href='/posts?tag=#{CGI::escape(tag)}' class='tag'>#{tag}</a>\"#CGI::escape() 메소드는 태그에서 사용할 수 있는 특수문자를 이스케이핑하기 위한 것이다.\n  end.join(', ').html_safe\nend\n  \nend\n","undoManager":{"mark":2,"position":7,"stack":[[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":16,"column":2},"end":{"row":17,"column":0}},"text":"\n"},{"action":"insertText","range":{"start":{"row":17,"column":0},"end":{"row":17,"column":2}},"text":"  "}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":17,"column":2},"end":{"row":18,"column":0}},"text":"\n"},{"action":"insertText","range":{"start":{"row":18,"column":0},"end":{"row":18,"column":2}},"text":"  "}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":17,"column":2},"end":{"row":17,"column":16}},"text":"def icon(font)"},{"action":"insertText","range":{"start":{"row":17,"column":16},"end":{"row":18,"column":0}},"text":"\n"},{"action":"insertLines","range":{"start":{"row":18,"column":0},"end":{"row":19,"column":0}},"lines":["    \"<span class='glyphicon glyphicon-#{font}'></span>\".html_safe"]},{"action":"insertText","range":{"start":{"row":19,"column":0},"end":{"row":19,"column":5}},"text":"  end"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":20,"column":2},"end":{"row